import Foundation

enum NoteDetails {
    // MARK: Use cases
    enum ShowNoteDetails {
        struct Request {
            let note: Note?
        }

        struct Response {
            let noteHeader: String?
            let noteText: String?
            let noteDate: Date?
        }

        struct ViewModel {
            let noteHeader: String
            let noteText: String
            let noteDate: String
        }
    }

    enum CheckNoteIsEmpty {
        struct Request {
            let noteHeader: String
            let noteText: String
            let noteDate: Date
        }

        struct Response {
            let isEmptyNote: Bool
        }

        struct ViewModel {
            let isEmptyNote: Bool
        }
    }
}
